import React from 'react'
import MessageList from './components/MessageList'
import Header from './components/Header'
import Sendform from './components/Sendform'
import {sendMessage} from './endpoints'


class App extends React.Component {
    state = {
      userInput: ""
    }
    handleSendMessage = async() => {
      try{
        let r = await sendMessage(this.state.userInput);
        console.log(r);
      }
      catch(e){
        console.log(e);
      }
    }

    handleUserInputChange = (message) => {
      this.state.userInput = message
    }

    render() {
        return (
            <div className="app">
                <Header />
                <MessageList />
                <Sendform onClick={this.handleUserInputChange} userInput={this.state.userInput}/>
            </div>
        );
    }
}

export default App
