import React from 'react'
import PropTypes from 'prop-types'
import sendImage from '../images/Send.png'
import '../style.css'



class Sendform extends React.Component{
    state = {
      userInput: this.props.userInput
    }



    render(){
      return (
        <form className="send-message-form">
        <input placeholder = "¿Qué quieres saber?" type = "text" onChange={this.props.onUserInputChange}/>
          <button type="submit" onClick={() => {this.props.send(this.state.userInput)}}>
          <img src={sendImage} alt="Send Message" height="40" width="40"/>
          </button>
        </form>
      )
    }
}

Sendform.propTypes = {
  userInput:PropTypes.string.isRequired,
  send:PropTypes.func.isRequired,
  onUserInputChange:PropTypes.func.isRequired
}
export default Sendform
