import React from 'react'
import profile from '../images/Bot.png'
import '../style.css'
import { getEvent } from '../endpoints'
import Message from './Message'
import styled, { css } from 'styled-components';

const TEST_DATA = [
    {
        date: '8:30',
        sender: false,
        text: 'Hola soy AyudaPe! Tu asistente personal de Yape.'
    },
    {
        date: '8:31',
        sender:true,
        text: 'Habla causa'
    },
    {
        date: '8:32',
        sender:false,
        text: '¿Qué necesitas saber?'
    },
    {
        date: '8:32',
        sender:true,
        text: 'que haces?'
    }
]

const Wrapper = styled.div`
  display:flex;

`;

class MessageList extends React.Component {

    render() {
      return (
        <div className="message-list">
            <Wrapper>
            {TEST_DATA.map((message, index) => {
                return (
                  <Message text={message.text} sender={message.sender}/>
                )
            })}
            </Wrapper>
        </div>
      )
    }
}
export default MessageList;
